import React from 'react';

const Cake = ({ cake }) => {
  const { cakeName, ingredients, price, rating } = cake;

  return (
    <div>
      <h2>{cakeName}</h2>
      <p>Rating: {rating}</p>
      <p>Price: ${price}</p>
      <h3>Ingredients:</h3>
      <ul>
        {ingredients.map((ingredient, index) => (
          <li key={index}>{ingredient}</li>
        ))}
      </ul>
    </div>
  );
};

export default Cake;
