import React from 'react';

const Cake = ({ cake }) => (
  <div>
    <h2>{cake.cakeName}</h2>
    <p>Rating: {cake.rating}</p>
    <p>Price: ${cake.price}</p>
    <h3>Ingredients:</h3>
    <ul>
      {cake.ingredients.map((ingredient, index) => (
        <li key={index}>{ingredient}</li>
      ))}
    </ul>
  </div>
);

export default Cake;
